    // }
    // else if(temp_XET_num > 0){
    //   ERC20(XET_address).transfer(msg.sender,temp_XET_num);
    // }
    ERC20(XET_address).transfer(msg.sender,XETdistribute(XET_Distribute_addr).estimate_out( amount));

    // update blockNumber and balance
    _updatePoolShot();
  }

