//SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "../VotePowerQueue.sol";
import "./UnstakeQueue.sol";

interface XCFXExchange{
    function addTokens(address _to, uint256 _value) external;
    function burnTokens(address account, uint256 amount) external;
    function balanceOf(address account) external view returns(uint256);
    function totalSupply() external view returns(uint256);
}

///
///  @title eSpace PoSPool
///
contract ESpacePoSPoolmini is Ownable,Initializable {
  using SafeMath for uint256;
  using EnumerableSet for EnumerableSet.AddressSet;
  using VotePowerQueue for VotePowerQueue.InOutQueue;
  using UnstakeQueue for UnstakeQueue.Queue;

  uint256 private constant RATIO_BASE = 10000;
  uint256 private constant ONE_DAY_BLOCK_COUNT = 3600 * 24;
  uint256 private CFX_COUNT_OF_ONE_VOTE = 1000;
  uint256 private CFX_VALUE_OF_ONE_VOTE = 1000 ether;


  // ======================== Pool config =========================
  // wheter this poolContract registed in PoS
  bool public birdgeAddrSetted;
  address private _bridgeAddress;
  // ratio shared by user: 1-10000
  uint256 public poolUserShareRatio = 9000;
  // lock period: 14 days
  uint256 public _poolLockPeriod_slow = ONE_DAY_BLOCK_COUNT * 14;
  uint256 public _poolLockPeriod_fast = ONE_DAY_BLOCK_COUNT * 7;
  string public poolName; // = "UNCLEON HUB";

  // ======================== Contract states =========================
  // global pool accumulative reward for each cfx
  uint256 public accRewardPerCfx;  // start from 0

  PoolSummary private _poolSummary;
  PoolAccounts private _poolAccounts;
  mapping(address => UserSummary) private userSummaries;

  VotePowerQueue.InOutQueue private Inqueues;
  // VotePowerQueue.InOutQueue private Outqueues;
  mapping(address => VotePowerQueue.InOutQueue) private userOutqueues;

  PoolShot internal lastPoolShot;
  // mapping(address => UserShot) internal lastUserShots;

  EnumerableSet.AddressSet private stakers;
  // Unstake votes queue
  UnstakeQueue.Queue public unstakeQueue;  //private--debug

  // Currently withdrawable CFX
  // uint256 public withdrawableCfx;
  // Votes need to cross from eSpace to Core
  uint256 public crossingVotes;

  // ======================== XCFX use ===================================
  address public owner_addr;
  address public admin_addr;

  address XCFX_address;
  address Storage_addr;
  address bridge_storage_interests;
  //address XET_Distribute_addr;
  address zero_addr=address(0x0000000000000000000000000000000000000000);

  // ======================== Struct definitions =========================
  /// @title PoolSummary
  /// @custom:field totalvotes
  /// @custom:field lockingvotes
  /// @custom:field lockedvotes
  /// @custom:field unlockingvotes
  /// @custom:field unlockedvotes
  struct PoolSummary {
    uint256 totalvotes;
    uint256 lockingvotes;
    uint256 lockedvotes;
    uint256 unlockingvotes;
    uint256 unlockedvotes;
  }

  /// @title PoolAccounts
  /// @custom:field totalxcfxs
  /// @custom:field totalinterests
  /// @custom:field cfxstillinstore
  /// @custom:field remainedcfx
  struct PoolAccounts {
    uint256 totalxcfxs;
    uint256 totalinterests;
    uint256 cfxstillinstore;
    uint256 remainedcfx;
  }

  /// @title UserSummary
  /// @custom:field unlocking
  /// @custom:field unlocked
  struct UserSummary {
    uint256 unlocking;
    uint256 unlocked;
  }

  struct PoolShot {
    uint256 available; // XCFX SUM (1 ether XCFX === 1)
    uint256 balance;
    uint256 blockNumber;
  } 

  // ======================== Modifiers =========================
  modifier onlyRegisted() {
    require(birdgeAddrSetted, "Pool is not setted");
    _;
  }

  modifier onlyBridge() {
    require(msg.sender == _bridgeAddress, "Only bridge is allowed");
    _;
  }

  // ======================== Helpers =========================
  function _selfBalance() internal view virtual returns (uint256) {
    return address(this).balance;
  }

  function _blockNumber() internal view virtual returns (uint256) {
    return block.number;
  }

  function _userShareRatio(address _user) public view returns (uint256) {
    return poolUserShareRatio;
  }

  // ======================== Events =========================

  event IncreasePoSStake(address indexed user, uint256 votePower);

  event DecreasePoSStake(address indexed user, uint256 votePower);

  event WithdrawStake(address indexed user, uint256 votePower);

  event ClaimInterest(address indexed user, uint256 amount);

  //event RatioChanged(uint256 ratio);

  // ======================== Init methods =========================

  // call this method when depoly the 1967 proxy contract
  function initialize() public initializer {
    CFX_COUNT_OF_ONE_VOTE = 1000;
    CFX_VALUE_OF_ONE_VOTE = 1000 ether;
    poolUserShareRatio = 9000;
    owner_addr = msg.sender;
    _poolLockPeriod_slow = ONE_DAY_BLOCK_COUNT * 14;
    _poolLockPeriod_fast = ONE_DAY_BLOCK_COUNT * 7;
  }

  // ======================== Contract methods =========================

  //--------------------------------------------------------------------all new function for XCFX  ↓

  modifier only_admin() {
        require(msg.sender == admin_addr, 'Must admin');
        _;
    }
  function set_admin(address _admin_addr) external onlyOwner {
        admin_addr=_admin_addr;
    } 
  function get_admin() external view returns(address){
    //require(_address==msg.sender,'1');
    return(admin_addr);
    }
  function set_Settings(address _XCFX_addr,address _S_addr) external onlyOwner {
        XCFX_address = _XCFX_addr;
        Storage_addr = _S_addr;
    }  

  function get_Settings() external view returns(address,address){
    return (XCFX_address,Storage_addr);
    }

  //
  // @title CFX_exchange_estim
  // @dev _amount The amount of CFX to stake
  //
  function CFX_exchange_estim(uint256 _amount) public view returns(uint256){
    return (_amount.mul(_poolAccounts.totalxcfxs).div(_poolAccounts.totalxcfxs+_poolAccounts.totalinterests));
    }

  function CFX_exchange_XCFX() external payable {
    require(msg.value>0 , 'must > 0');
    address payable receiver = payable(_bridgeAddress);
    receiver.transfer(msg.value);
    uint256 xcfx_exchange = CFX_exchange_estim(msg.value);
    XCFXExchange(XCFX_address).addTokens(msg.sender, xcfx_exchange);

    _poolAccounts.totalxcfxs += xcfx_exchange;
    if(xcfx_exchange+_poolAccounts.cfxstillinstore >= 1000 ether){
      uint256 votePower = (xcfx_exchange+_poolAccounts.cfxstillinstore).div(1000 ether);
      crossingVotes += votePower; //IncreaseStake method
      _poolAccounts.cfxstillinstore = _poolAccounts.cfxstillinstore + xcfx_exchange - votePower.mul(1000 ether);
      Inqueues.enqueue(VotePowerQueue.QueueNode(votePower, _blockNumber() + _poolLockPeriod_slow));
      _poolSummary.totalvotes += votePower;
      _poolSummary.lockingvotes += votePower;

    }

    emit IncreasePoSStake(msg.sender, msg.value);

  }

  function XCFX_burn_estim(uint256 _amount) public view returns(uint256){
    return (_amount.mul(_poolAccounts.totalxcfxs+_poolAccounts.totalinterests).div(_poolAccounts.totalxcfxs));
    }

  function XCFX_burn(uint256 _amount, uint256 _mode) public virtual onlyRegisted returns(uint256){
    require(_amount<=XCFXExchange(XCFX_address).balanceOf(msg.sender),"Exceed balance");

    XCFXExchange(XCFX_address).burnTokens(msg.sender, _amount);
    uint256 cfx_back = XCFX_burn_estim(_amount);
    _poolAccounts.totalxcfxs -= _amount;
    _poolSummary.lockedvotes += Inqueues.collectEndedVotes();
    if(_mode == 0){
      require(cfx_back<=_poolAccounts.remainedcfx,"Exceed zero time exchange amount");
    }
    else if(_mode == 1){
      require(cfx_back<=_poolSummary.lockedvotes.mul(1000 ether),"Exceed fast exchange amount");
    }
    else if(_mode == 2){
      require(cfx_back<=_poolSummary.totalvotes.mul(1000 ether),"Exceed exchange limit");
    }
    else return 0;

    uint256 unstakeVotePowers;

    if(cfx_back > _poolAccounts.remainedcfx){
      unstakeVotePowers = (cfx_back - _poolAccounts.remainedcfx).div(1000 ether) + 1;
      unstakeQueue.enqueue(UnstakeQueue.Node(unstakeVotePowers));  //decreaseStake method
      _poolAccounts.remainedcfx = _poolAccounts.remainedcfx + unstakeVotePowers.mul(1000 ether) - cfx_back;
      _poolSummary.totalvotes -= unstakeVotePowers;
      if(unstakeVotePowers<=_poolSummary.lockedvotes){
        _poolSummary.lockedvotes = _poolSummary.lockedvotes - unstakeVotePowers;
      }
      else if(unstakeVotePowers>_poolSummary.lockedvotes){
        _poolSummary.lockedvotes = 0;
        _poolSummary.lockingvotes = _poolSummary.lockingvotes - (unstakeVotePowers-_poolSummary.lockedvotes);
      }
      _poolSummary.unlockingvotes += unstakeVotePowers;

    }
    userOutqueues[msg.sender].enqueue(VotePowerQueue.QueueNode(cfx_back, _blockNumber() + _poolLockPeriod_fast));

    userSummaries[msg.sender].unlocking += cfx_back;

    uint256 temp_amount = userOutqueues[msg.sender].collectEndedVotes();
    userSummaries[msg.sender].unlocked += temp_amount;
    userSummaries[msg.sender].unlocking -= temp_amount;
    _poolSummary.unlockedvotes += temp_amount;
    _poolSummary.unlockingvotes -= temp_amount;

    emit DecreasePoSStake(msg.sender, _amount);

  }

  function getback_CFX(uint256 _amount) public virtual onlyRegisted {
     withdraw(_amount);
  }  
